import React from "react";
import { Link } from "react-router-dom";

const Card = ({ item }) => {
    console.log(item);
    return (
        <Link className="link" to={`/product/${item.id}`}>
            <div className="card">
                <div className="cardImage">
                    {item?.attributes.isNew && <span className="cardSpan">New Season</span>}
                    <img
                        src={
                            process.env.REACT_APP_UPLOAD_URL + item.attributes?.img?.data?.attributes?.url
                        }
                        alt=""
                        className="cardMainImg"
                    />
                    <img
                        src={
                            process.env.REACT_APP_UPLOAD_URL + item.attributes?.img2?.data?.attributes?.url
                        }
                        alt=""
                        className="cardSecondImg"
                    />
                </div>
                <h2>{item?.attributes.title}</h2>
                <div className="cardPrices">
                    <h3>${item.oldPrice || item?.attributes.price + 20}</h3>
                    <h3>${item?.attributes.price}</h3>
                </div>
            </div>
        </Link>
    );
};

export default Card;
